import React, { useEffect, useState } from 'react';
import { fetchLogs } from '../api';

export function Dashboard() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [sortBy, setSortBy] = useState('timestamp');
  const [sortOrder, setSortOrder] = useState('desc');
  const [filters, setFilters] = useState({ client: '', country: '' });

  const loadLogs = async (targetPage = page) => {
    setLoading(true);
    try {
      const data = await fetchLogs({
        page: targetPage,
        limit: 10,
        sortBy,
        sortOrder,
        filters
      });
      setLogs(data.logs || []);
      setPage(data.page);
      setTotalPages(data.totalPages);
    } catch (error) {
      console.error('Error loading logs:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadLogs();
  }, [sortBy, sortOrder]);

  const toggleSort = (field) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('asc');
    }
    setPage(1);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const applyFilters = () => {
    setPage(1);
    loadLogs(1);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Operations Dashboard</h1>

      {/* Filters */}
      <div className="flex gap-4 mb-4">
        <input
          name="client"
          value={filters.client}
          onChange={handleFilterChange}
          placeholder="Filter by Client"
          className="border px-3 py-1 rounded w-60"
        />
        <input
          name="country"
          value={filters.country}
          onChange={handleFilterChange}
          placeholder="Filter by Country"
          className="border px-3 py-1 rounded w-40"
        />
        <button
          onClick={applyFilters}
          className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
        >
          Apply Filters
        </button>
      </div>

      {loading ? (
        <p className="text-gray-600">Loading...</p>
      ) : (
        <>
          {/* Table */}
          <table className="border border-collapse w-full">
            <thead>
              <tr className="bg-gray-100">
                <th
                  className="border px-3 py-2 cursor-pointer"
                  onClick={() => toggleSort('transactionSourceName')}
                >
                  Client {sortBy === 'transactionSourceName' && (sortOrder === 'asc' ? '▲' : '▼')}
                </th>
                <th
                  className="border px-3 py-2 cursor-pointer"
                  onClick={() => toggleSort('country_code')}
                >
                  Country {sortBy === 'country_code' && (sortOrder === 'asc' ? '▲' : '▼')}
                </th>
                <th className="border px-3 py-2 text-right">Total Records</th>
                <th className="border px-3 py-2 text-right">Indexed</th>
                <th className="border px-3 py-2 text-right">Failed</th>
                <th
                  className="border px-3 py-2 cursor-pointer"
                  onClick={() => toggleSort('timestamp')}
                >
                  Timestamp {sortBy === 'timestamp' && (sortOrder === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {logs.length === 0 ? (
                <tr>
                  <td colSpan="6" className="text-center py-4">
                    No logs found.
                  </td>
                </tr>
              ) : (
                logs.map((log) => (
                  <tr key={log._id} className="hover:bg-gray-50">
                    <td className="border px-2">{log.transactionSourceName}</td>
                    <td className="border px-2">{log.country_code}</td>
                    <td className="border px-2 text-right">
                      {log.progress?.TOTAL_RECORDS_IN_FEED ?? '—'}
                    </td>
                    <td className="border px-2 text-right">
                      {log.progress?.TOTAL_JOBS_SENT_TO_INDEX ?? '—'}
                    </td>
                    <td className="border px-2 text-right">
                      {log.progress?.TOTAL_JOBS_FAIL_INDEXED ?? '—'}
                    </td>
                    <td className="border px-2">
                      {new Date(log.timestamp).toLocaleString()}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex items-center justify-between mt-4">
            <button
              disabled={page <= 1}
              onClick={() => loadLogs(page - 1)}
              className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
            >
              Prev
            </button>
            <span className="text-sm">
              Page {page} of {totalPages}
            </span>
            <button
              disabled={page >= totalPages}
              onClick={() => loadLogs(page + 1)}
              className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
