const BASE_URL = 'http://localhost:4000/api';

export const fetchLogs = async (page = 1, limit = 10) => {
  const res = await fetch(`${BASE_URL}/logs?page=${page}&limit=${limit}`);
  return res.json();
};

export const importLogs = async (clearOld = false) => {
  const res = await fetch(`${BASE_URL}/import-feed`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ clearOld })
  });
  return res.json();
};

export const sendPrompt = async (prompt) => {
  const res = await fetch(`${BASE_URL}/ai-query`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ prompt })
  });
  return res.json();
};
