import { useEffect, useState } from 'react';
import { fetchLogs, importLogs } from '../services/api';

export default function Dashboard() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadLogs();
  }, []);

  const loadLogs = async () => {
    setLoading(true);
    const data = await fetchLogs();
    setLogs(data);
    setLoading(false);
  };

  const handleImport = async () => {
    await importLogs(true);
    await loadLogs();
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Operations Dashboard</h1>
      <button onClick={handleImport} className="bg-blue-500 text-white px-4 py-2 rounded">
        Re-import logs
      </button>

      {loading ? (
        <p className="mt-4">Loading...</p>
      ) : (
        <table className="mt-4 border border-collapse w-full">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-2">Client</th>
              <th className="border px-2">Country</th>
              <th className="border px-2">Total Records</th>
              <th className="border px-2">Indexed</th>
              <th className="border px-2">Failed</th>
              <th className="border px-2">Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {logs.map(log => (
              <tr key={log._id}>
                <td className="border px-2">{log.transactionSourceName}</td>
                <td className="border px-2">{log.country_code}</td>
                <td className="border px-2">{log.progress?.TOTAL_RECORDS_IN_FEED}</td>
                <td className="border px-2">{log.progress?.TOTAL_JOBS_SENT_TO_INDEX}</td>
                <td className="border px-2">{log.progress?.TOTAL_JOBS_FAIL_INDEXED}</td>
                <td className="border px-2">{new Date(log.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
