import { useEffect, useState } from 'react';
import { fetchLogs } from '../services/api';

export function Dashboard() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  useEffect(() => {
    loadLogs(1);
  }, []);

  const loadLogs = async (pageNum) => {
    setLoading(true);
    const data = await fetchLogs(pageNum, 10);
    setLogs(data.logs);
    setLoading(false);
    setTotalPages(data.totalPages);
    setPage(data.page);
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Operations Dashboard</h1>
      {loading ? (
        <p className="mt-4">Loading...</p>
      ) : (
        <div className="mt-4">
          <table className="border border-collapse w-full text-sm">
            <thead>
              <tr className="bg-gray-100">
                <th className="border px-3 py-2 text-left">Client</th>
                <th className="border px-3 py-2 text-left">Country</th>
                <th className="border px-3 py-2 text-right">Total Records</th>
                <th className="border px-3 py-2 text-right">Indexed</th>
                <th className="border px-3 py-2 text-right">Failed</th>
                <th className="border px-3 py-2 text-left">Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log) => (
                <tr key={log._id}>
                  <td className="border px-3 py-2">{log.transactionSourceName}</td>
                  <td className="border px-3 py-2">{log.country_code}</td>
                  <td className="border px-3 py-2 text-right">{log.progress?.TOTAL_RECORDS_IN_FEED?.toLocaleString()}</td>
                  <td className="border px-3 py-2 text-right">{log.progress?.TOTAL_JOBS_SENT_TO_INDEX?.toLocaleString()}</td>
                  <td className="border px-3 py-2 text-right">{log.progress?.TOTAL_JOBS_FAIL_INDEXED?.toLocaleString()}</td>
                  <td className="border px-3 py-2">{new Date(log.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination Controls */}
          <div className="flex justify-center items-center mt-4 gap-4">
            <button
              onClick={() => loadLogs(page - 1)}
              disabled={page === 1}
              className="px-4 py-1 bg-blue-500 text-white rounded disabled:bg-gray-300"
            >
              Previous
            </button>
            <span className="text-sm text-gray-700">
              Page {page} of {totalPages}
            </span>
            <button
              onClick={() => loadLogs(page + 1)}
              disabled={page === totalPages}
              className="px-4 py-1 bg-blue-500 text-white rounded disabled:bg-gray-300"
            >
              Next
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
